package com.hmx.shop.utils;

import java.nio.charset.StandardCharsets;
import java.security.KeyFactory;
import java.security.NoSuchAlgorithmException;
import java.security.PrivateKey;
import java.security.PublicKey;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.security.spec.RSAPublicKeySpec;
import java.security.spec.X509EncodedKeySpec;
import java.util.Base64;

public class ConstantUtils {


    public static final String PUBLIC_KEY = "";

    public static final String PRIVATE_KEY = "";

    public static PrivateKey generatePrivateKey() throws NoSuchAlgorithmException, InvalidKeySpecException {
        byte[] decode = Base64.getDecoder().decode(PRIVATE_KEY);
        PKCS8EncodedKeySpec pkcs8EncodedKeySpec = new PKCS8EncodedKeySpec(decode);
        KeyFactory rsa = KeyFactory.getInstance("RSA");
        return rsa.generatePrivate(pkcs8EncodedKeySpec);
    }

    public static PublicKey generatePublickey() throws NoSuchAlgorithmException, InvalidKeySpecException {
        byte[] decode = Base64.getDecoder().decode(PUBLIC_KEY);
        X509EncodedKeySpec pkcs8EncodedKeySpec = new X509EncodedKeySpec(decode);
        KeyFactory rsa = KeyFactory.getInstance("RSA");
        return rsa.generatePublic(pkcs8EncodedKeySpec);
    }




}
